.fr-note {
  padding: var(--card-padding, var(--space-m));
  border-radius: var(--card-radius, var(--radius));
  background: var(--warning-light, #FDEBB5);
  color: var(--warning-ultra-dark, #544002);
  margin-inline: auto;
}

.fr-note * + :is(h2, h3, h4) {
  margin-top: var(--heading-spacing);
  color: var(--text-dark);
}

.fr-note a {
  text-decoration: underline;
}

.content-grid-yankee {
  --media-span: 7;
  --heading-span: 7;

  display: grid;
  position: relative;
  isolation: isolate;
  gap: var(--content-gap);

  @media (min-width: 768px) {
		grid-template-columns: var(--grid-12);
  	grid-template-rows: repeat(3, 1fr);
	}

}

.content-grid-yankee__heading-wrapper {
  @media (min-width: 768px) {
		grid-column: 1 / span var(--heading-span);
    grid-row: 1;
    translate: 0 var(--space-l);
	}
}
.content-grid-yankee__media-wrapper {
  @media (min-width: 768px) {
		grid-column: calc(12 - var(--media-span)) / -1;
  	grid-row: 1 / -1;

    img {
      aspect-ratio: 1/1;
    }
	}
  height: 100%;
  width: 100%;
  z-index: -1;
	border-radius: var(--radius);
  overflow: clip;

  img {
    --radius: 0;
  }
}
.content-grid-yankee__content-wrapper {
  @media (min-width: 768px) {
		grid-column: 1 / span 11;
    grid-row: 3;
    align-self: end;
	}
  border-radius: var(--radius);
}

/* Auto-responsive columns */
.content-grid-yankee__content {
	--col-count: 2;
	--col-min-width: 250px;
	--col-rule-style: solid;
	--col-rule-width: 0;
	--col-rule-color: var(--black);
	--col-gap: var(--grid-gap);
	--row-gap: var(--grid-gap);
}

.content-grid-yankee__content {
	display: block !important;
	columns: var(--col-count) var(--col-min-width);
	column-rule-style: var(--col-rule-style);
	column-rule-width: var(--col-rule-width);
	column-rule-color: var(--col-rule-color);
	gap: var(--col-gap);
  font-size: var(--text-l);
}

.content-grid-yankee__content > * {
		break-inside: avoid;
	margin-block-end: var(--row-gap);
}

.content-grid-yankee__heading {
  line-height: 1;
}

.content-grid-yankee__heading .line {
  display: inline-block;
  padding: .15em .25em;
  margin-block: .075em;
  background: var(--white);
  background-repeat: no-repeat;
  border-radius: var(--radius);
  width: fit-content;
}

/* 
Begin List None
Change selector to .social-proof-yankee > ul" if
using on a parent container or rich text element.
*/
.social-proof-yankee {
		list-style: none;
	padding-inline-start: 0;
	margin-block: 0;
}

/* End List None */

.social-proof-yankee__card:not(:last-child)::after {
  @media (min-width: 992px) {
		content: '';
    border: var(--border-dark);
    position: absolute;
    top: 0;
    left: calc(100% + var(--grid-gap) / 2);
    height: 100%;
    width: 1px;
	}
}
