.gallery-bravo {
  --nb-cols: 2;
  --nb-rows: 3;
  --grid-areas:
    "featured featured"
    "area2 area3"
    "area4 cta";  

  display: grid;
  grid-template-columns: repeat(var(--nb-cols), minmax(0, 1fr));
  grid-template-rows: repeat(var(--nb-rows), min-content);
  gap: calc(var(--grid-gap)/2);
  grid-template-areas: var(--grid-areas);  
  align-items: center;
  list-style-type: none;
  padding: 0;
  margin: 0;
}

/*
 * Hide media wrappers that aren't the button,
 * after the 6th one
*/
.gallery-bravo > *:nth-child(n+6 of .gallery-bravo__media-wrapper:not(.gallery-bravo__media-wrapper--button)) {
  display: none;
}


/* Mobile grid placement */
@media screen and (max-width: 767px) {

  .gallery-bravo >:nth-child(2 of .gallery-bravo__media-wrapper) {
    grid-area: featured;
  }
  .gallery-bravo >:nth-child(3 of .gallery-bravo__media-wrapper) {
    grid-area: area2;
  }
  .gallery-bravo >:nth-child(4 of .gallery-bravo__media-wrapper) {
    grid-area: area3;
  }
  .gallery-bravo >:nth-child(5 of .gallery-bravo__media-wrapper) {
    grid-area: area4;
  }

  .gallery-bravo >:nth-child(1 of .gallery-bravo__media-wrapper),
  .gallery-bravo__media-wrapper--button {
    grid-area: cta;
  }
}


/* Desktop grid + grid placement */
@media screen and (min-width: 768px) {
	.gallery-bravo {
    --nb-cols: 4;
	  --nb-rows: 2;
    --grid-areas:
      "featured featured area2 area3"
      "featured featured area4 cta"
      ;
	}

  .gallery-bravo >:nth-child(1 of .gallery-bravo__media-wrapper) {
    grid-area: featured;
  }
  .gallery-bravo >:nth-child(5 of .gallery-bravo__media-wrapper),
  .gallery-bravo__media-wrapper--button {
    grid-area: cta;
  }

}

.gallery-bravo__media-wrapper,
.gallery-bravo__media-wrapper a {
  inline-size: 100%;
  block-size: 100%;
}

.gallery-bravo__media-wrapper {
  min-inline-size: 100%;
  aspect-ratio: 1/1;
  border-radius: var(--radius);  
  overflow: clip;
  contain: paint;
}

.gallery-bravo__media-wrapper a {
  display: block;
}

.gallery-bravo__media-wrapper img {
  position: relative;
  isolation: isolate;
  aspect-ratio: 16/9;
  transition: transform var(--transition-duration) var(--transition-timing) var(--transition-delay);

}

.gallery-bravo__media-wrapper:hover img {
  transform: scale(1.05);
}

.gallery-bravo__media-wrapper--button {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  position: relative;
  isolation: isolate;
  gap: .5em;
  pointer-events: none;
}

.gallery-bravo__media-wrapper--button::before {
  content: '';
  position: absolute;
  inset: 0;
	background: var(--neutral-trans-50);
  z-index: -1;
}

.gallery-bravo__media-wrapper--button :is(svg, i) {
  color: var(--text-light);
  order: -1;
  height: 2em;
  width: 2em;
}
.gallery-bravo__media-wrapper--button p {
  text-align: center;
  color: var(--text-light);
}
