/* This menu automatically turns the last link in your menu into a button and positions it to the right. This is better than using a physical button outside your menu, because it retains a working link for the CTA item in your mobile menu and maintains proper semantic menu/list structure. All styling can be adjusted below */

.fr-nav-delta {
  --link-color: var(--primary);
  --link-color-hover: var(--primary-hover);
  --link-color-active: var(--primary-light);
  --button-color: var(--primary);
  --button-color-hover: var(--primary-hover);  
  --button-color-active: var(--primary-dark);    
  --button-text: var(--text-light);  
  --button-text-hover: var(--text-light); 
  --button-text-active: var(--primary-light);   
  --toggle-gap: .5em;
  --sub-li-block-padding: 1em;
  --sub-li-inline-padding: 1.5em;  
  --sub-li-bg: transparent;
  --sub-li-bg-hover: var(--neutral-light);
}

.fr-nav-delta > nav > ul {
  align-items: center;
}

.fr-nav-delta ul.bricks-mobile-menu {
  display: flex;
  align-items: center;
  padding-block: var(--space-xxl);
}

.show-mobile-menu ul.bricks-mobile-menu {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
}

.fr-nav-delta {
  align-items: center; 
}

.fr-nav-delta nav {
  width: 100%;
}

/* ACSS text max-width fix */
.fr-nav-delta:not(.show-mobile-menu) li.menu-item:not(.sub-menu li) {
  max-width: fit-content;
}

/* Center Navigation when Nav is set to width:100% */
.fr-nav-delta .bricks-nav-menu > li:first-child,
.fr-nav-delta .bricks-nav-menu > li:last-child {
  margin-inline-start: auto;
}

/* Button Styling */
.fr-nav-delta ul.bricks-nav-menu > li:last-of-type a {
  background-color: var(--button-color);
  border-color: var(--button-color);
  color: var(--button-text);
  border-width: var(--btn-border-size);
  border-radius: var(--btn-radius);
  border-style: var(--btn-border-style);
  min-inline-size: var(--btn-min-width);
  line-height: var(--btn-line-height);  
  letter-spacing: var(--btn-letter-spacing);
  text-transform: var(--btn-text-transform);
  text-decoration: var(--btn-text-decoration);
  font-style: var(--btn-font-style);
  font-weight: var(--btn-font-weight);
  padding: var(--btn-padding-block) var(--btn-padding-inline);
  justify-content: center;
  text-align: center;
}
/* Button Hover Styling */
.fr-nav-delta ul.bricks-nav-menu > li:last-of-type a:hover {
  background-color: var(--button-color-hover);
  border-color: var(--button-color-hover);
  color: var(--button-text-hover);
}

.fr-nav-delta.fr-nav-delta.brxe-nav-menu .bricks-nav-menu li.current-menu-item:last-child > a {
  color: var(--button-text-active);
  background-color: var(--button-color-active);
  border-color: var(--button-color-active);  
}

.fr-nav-delta .brx-submenu-toggle {
  display: flex;
  flex-direction: row;
  gap: var(--toggle-gap);
}

.fr-nav-delta .brx-submenu-toggle button {
  margin: 0;
  padding: 0;
}

.fr-nav-delta .sub-menu {
  min-inline-size: max-content;
}

.fr-nav-delta .sub-menu li {
  background-color: var(--sub-li-bg);
  padding-block: var(--sub-li-block-padding);
  padding-inline: var(--sub-li-inline-padding);  
  position: relative;
}

.fr-nav-delta ul.bricks-mobile-menu .sub-menu li {
  padding-block: 0;  
  padding-inline: var(--sub-li-inline-padding);  
}

.fr-nav-delta.show-mobile-menu .sub-menu li > ul li {
  padding-block: 0;  
  padding-inline: var(--sub-li-inline-padding) 0;  
}

.fr-nav-delta:not(.show-mobile-menu) .sub-menu li:hover {
  background-color: var(--sub-li-bg-hover);
}

.fr-header-delta {
  margin-inline: auto;
  /* Uncomment line below if you want glass blur effect */
	/* backdrop-filter: blur(5px); */
}
