settings._cssCustom:
.fr-icon-button-charlie {
  --btn-bg-color: var(--primary);
  --border-radius: var(--btn-border-radius);
  --icon-color: var(--text-light);
  --icon-color-hover: var(--text-light);
  --icon-size: 30px;

  transition: color var(--transition-duration) var(--transition-timing) var(--transition-delay);
  padding-inline: calc(var(--btn-padding-inline) / 2);
  display: inline-flex;
  position: relative;
  z-index: 1;
}

.fr-icon-button-charlie:hover, .fr-icon-button-charlie:active {
  color: var(--white);
}

.fr-icon-button-charlie__icon {
  transition: transform var(--transition-duration) var(--transition-timing) var(--transition-delay);
  transform: translateX(-5px);
}

.fr-icon-button-charlie:hover .fr-icon-button-charlie__icon, .fr-icon-button-charlie:active .fr-icon-button-charlie__icon {
  transform: translateX(calc((var(--btn-padding-inline) / 2) - 12px));
}

.fr-icon-button-charlie__icon:is(svg, i) {
	min-height: var(--icon-size);
  min-width: var(--icon-size);
  font-size: var(--icon-size);
  fill: var(--icon-color);
  color: var(--icon-color);
}

.fr-icon-button-charlie__icon:is(svg, i):hover {
  fill: var(--icon-color-hover);
  color: var(--icon-color-hover);  
}

.fr-icon-button-charlie__icon-wrapper::before {
  content: '';  
  position: absolute;
  left: 0;
  transform-origin: center left; 
  width: var(--btn-padding-block);
  height: 2px;
  background-color: var(--icon-color);
  opacity: 0;
  z-index: 1;
  transform: translateX(calc(var(--btn-padding-inline) * -0.5));
  transition: 
    opacity var(--transition-duration) var(--transition-timing) var(--transition-delay),
    transform var(--transition-duration) var(--transition-timing) var(--transition-delay);
}

.fr-icon-button-charlie:hover .fr-icon-button-charlie__icon-wrapper::before, .fr-icon-button-charlie:active .fr-icon-button-charlie__icon-wrapper::before {
  opacity: 1;
  transform: translateX(calc((var(--btn-padding-inline) / 2)));
}

.fr-icon-button-charlie::after {
  content: '';
  background-color: var(--btn-bg-color);
  height: calc(((var(--btn-padding-block) * 2) + 1em) + (var(--btn-border-width) * 2));
  width: 100%;
  max-width: calc(((var(--btn-padding-block) * 2) + 1em) + (var(--btn-border-width) * 2));
  position: absolute;
  inset-inline-start: 0;
  border-radius: var(--border-radius);
  z-index: -1;
  transition: max-width var(--transition-duration) var(--transition-timing) var(--transition-delay);
}

.fr-icon-button-charlie:hover::after, .fr-icon-button-charlie:active::after {
  max-width: 100%;
}

@media (hover: none) {
  .fr-icon-button-charlie:active {
    color: var(--white);
  }

  .fr-icon-button-charlie:active .fr-icon-button-charlie__icon {
    transform: translateX(calc((var(--btn-padding-inline) / 2) - 2px));
  }

  .fr-icon-button-charlie:active .fr-icon-button-charlie__icon-wrapper::before {
    opacity: 1;
    transform: translateX(calc((var(--btn-padding-inline) / 2)));
  }

  .fr-icon-button-charlie:active::after {
    max-width: 100%;
  }
}

.fr-icon-button-charlie__label {
  font-weight: var(--btn-font-weight); 
  border-width: var(--btn-border-width);
  border-style: var(--btn-border-style);
  border-color: transparent;
}
