.fr-menu-card-alpha {
  /* Set the featured box size by editing Menu Grid Alpha */
  --featured-box-offset-top-adjustment: 1.5;
  --featured-box-radius: var(--radius);
  --featured-box-background: var(--bg-light);
  --featured-box-border-width: 1px;
  --featured-box-border-color: var(--neutral-ultra-dark-trans-10);
  --featured-label-height: 26px;
  --featured-label-nudge: 2px;
  --featured-label-background: var(--bg-dark);
  --featured-label-text-color: var(--text-light);
  --featured-label-font-size: var(--text-xs);
  --featured-label-inline-padding: .5em;
  --featured-label-radius: var(--radius);
	--line-style: dashed; /* Accepts any border style */
  --line-color: var(--neutral-ultra-dark-trans-20);
  --line-nudge: 5px;
  position: relative;
}

/* 
Add the attribute "data-menu-item-style" and set its value to "featured" to make a menu item featured. Additionally, you need to add the attribute "data-featured-text" to your featured menu item to control the featured label text. As of the creation of this frame, featured label text will not render in the builder, but will display correctly on the front end.
*/


/* Box */
.fr-menu-card-alpha[data-featured-text]:not([data-featured-text=""])::before {
	content: "";
 	position: absolute;
  inset: calc(var(--featured-box-offset) * var(--featured-box-offset-top-adjustment) * -1) calc(var(--featured-box-offset) * -1) calc(var(--featured-box-offset) * -1) calc(var(--featured-box-offset) * -1);
  background: var(--featured-box-background);
  z-index: -1;
  border-radius: var(--featured-box-radius);
  border-width: var(--featured-box-border-width);
  border-style: solid;
  border-color: var(--featured-box-border-color);
}

/* Label */
.fr-menu-card-alpha[data-featured-text]:not([data-featured-text=""])::after {
  --half-label-height: calc(var(--featured-label-height) / 2);
	content: attr(data-featured-text);
  block-size: var(--featured-label-height);
  display: flex;
  align-items: center;
  position: absolute;
  padding-inline: var(--featured-label-inline-padding);
  inset-block-start: calc(((var(--featured-box-offset) * var(--featured-box-offset-top-adjustment)) + var(--half-label-height) + var(--featured-label-nudge, 0)) * -1);
  inset-inline-start: 0;
  background: var(--featured-label-background);
  color: var(--featured-label-text-color);
  font-size: var(--featured-label-font-size);
  line-height: 1;
  border-radius: var(--featured-label-radius);
}

.fr-menu-card-alpha__head::before {
	content: "";
  flex: 1;
  order: 2;
  border-block-end: 1px var(--line-style) var(--line-color);
  position: relative;
  inset-block-end: var(--line-nudge);
}
