.fr-note {
  padding: var(--card-padding, var(--space-m));
  border-radius: var(--card-radius, var(--radius));
  background: var(--warning-light, #FDEBB5);
  color: var(--warning-ultra-dark, #544002);
  margin-inline: auto;
}

.fr-note * + :is(h2, h3, h4) {
  margin-top: var(--heading-spacing);
  color: var(--text-dark);
}

.fr-note a {
  text-decoration: underline;
}

/* Begin Auto Grid */
.pricing-table-echo__grid {
	display: grid !important;
	grid-template-columns: var(--grid-template-columns);
	/* Set or change column count */
--column-count: 4;
	/* Set or change stacking aggressiveness */
--auto-grid-aggressiveness: .7;
	/* Don't touch the stuff below unless you have a PhD in CSS */
--min: calc(((var(--content-width) - ((var(--column-count) - 1) * var(--grid-gap))) / var(--column-count)) * var(--auto-grid-aggressiveness));
	--grid-template-columns: repeat(auto-fit, minmax(var(--min-formula), 1fr));
	--grid-template-columns-even: repeat(auto-fit, minmax(var(--min-formula), 1fr) minmax(var(--min-formula), 1fr));
	--min-formula: min(100%, max(var(--min), (100% - (var(--column-count) - 1) * var(--grid-gap)) / var(--column-count)));
	/* Special handling for children */
> * {
	min-width: 0;
	max-width: 100%;
	overflow-wrap: break-word;
}

gap: calc(var(--grid-gap));
	/* No touchey - redefine this variable if you want to change the gap, but don't replace the variable or your grid will break */
}

/* Force even columns */
.pricing-table-echo__grid {
	@media (min-width: 480px) {
	--grid-template-columns: var(--grid-template-columns-even) !important;
}

}

/* End Auto Grid */

/* 
Begin List None
Change selector to .pricing-table-echo__grid > ul" if
using on a parent container or rich text element.
*/
.pricing-table-echo__grid {
		list-style: none;
	padding-inline-start: 0;
	margin-block: 0;
}

/* End List None */

.pricing-card-echo {
  position: relative;
}

/* FEATURED OPTION STYLING */
.pricing-card-echo[data-featured-price-column] {
  .pricing-card-echo__header {
    border: 1px solid var(--primary);
    box-shadow: 0 0 0 5px var(--primary-trans-10);

    &::before {
      content: 'Popular';
      position: absolute;
      top: 0;
      left: 50%;
      translate: -50% -50%;
      line-height: 1;
      padding: .45em 1em;
      background: var(--primary);
      color: var(--primary-ultra-light);
      border-radius: 50vw;
      font-size: var(--text-xs);
      text-transform: uppercase;
      letter-spacing: .15em;
    }
  }
}

/* Concentric Radius */
/*
Parent element radius..
Read documentation if you're unsure how this all works.
*/
.pricing-card-echo__header {
	  --padding: var(--space-m);
	padding: var(--padding);
	border-radius: calc(var(--radius) + var(--padding) / 2);
}

/* Shadow */
.pricing-card-echo__header {
  box-shadow: 2px 2px 20px -10px var(--black-trans-30);
}

.pricing-table-echo {
  --tabs-wrapper-padding: .35em; /* For concentric radius */
}

.pricing-table-echo .fr-tabs__list.horizontal {
  gap: .5em;
}

.pricing-table-echo .fr-tabs__animation {
  border-radius: var(--radius);
  box-shadow: 2px 2px 10px -8px var(--black-trans-30);
}

.pricing-table-echo .plan-reference {
  color: var(--primary);
  font-style: italic;
}
