/* 
Begin List None
Change selector to .fr-social-alpha > ul" if
using on a parent container or rich text element.
*/
.fr-social-alpha {
  list-style: none;
  padding-inline-start: 0;
  margin-block: 0;
}

/* End List None */

.fr-profile-card-golf {
	--overlay-color: var(--black-trans-60);
  --transition-duration: .3s;
  --transition-function: ease-in-out;
  --transition-delay: .05s;
  --social-icon-size: 1.15em;
  --social-icon-color: var(--white);
  --social-icon-gap: .25em;
}

.fr-profile-card-golf {
	overflow: hidden;
}

.fr-profile-card-golf:focus-within :focus {
	outline: none;
  box-shadow: none;
}

.fr-profile-card-golf:focus-within {
	box-shadow: 0 0 40px 0px var(--black-trans-10);
  outline: none !important;
}

@media (min-width:767px) {

  /* Media */

  .fr-profile-card-golf__media {
    transition: var(--transition-duration) var(--transition-function);
  }

  .fr-profile-card-golf.is-hover .fr-profile-card-golf__media,
  .fr-profile-card-golf:focus-within .fr-profile-card-golf__media{
    scale: 1.1;
  }

  /* Overlay */

  .fr-profile-card-golf__overlay {
    opacity: 0;
    transition: calc(var(--transition-duration) + .35s) var(--transition-function);
  }

  .fr-profile-card-golf.is-hover .fr-profile-card-golf__overlay,
  .fr-profile-card-golf:focus-within .fr-profile-card-golf__overlay{
    opacity: 1;
  }

  /*** Content ***/

  /* Name */

  .fr-profile-card-golf__name {
    transition: calc(var(--transition-duration)) var(--transition-function);
    opacity: 0;
  }

  .fr-profile-card-golf.is-hover .fr-profile-card-golf__name,
  .fr-profile-card-golf:focus-within .fr-profile-card-golf__name{
    opacity: 1;
  }

  /* Title */

  .fr-profile-card-golf__title {
    translate: 0% 100%;
    opacity: 0;
    transition: calc(var(--transition-duration) - .05s) var(--transition-function);
  }

  .fr-profile-card-golf.is-hover .fr-profile-card-golf__title,
  .fr-profile-card-golf:focus-within .fr-profile-card-golf__title {
    translate: 0% 0%;
    opacity: 1;
  }

  /* Social Icons */

  .fr-profile-card-golf__social-icon {
    transition: var(--transition-duration) var(--transition-function);
    opacity: 0;
    translate: 0% 100%;
  }

  .fr-profile-card-golf.is-hover .fr-profile-card-golf__social-icon,
  .fr-profile-card-golf:focus-within .fr-profile-card-golf__social-icon {
    translate: 0% 0%;
    opacity: 1;
  }

  .fr-profile-card-golf__social-icon:nth-child(1) {
    transition-delay: var(--transition-delay);
  }
  .fr-profile-card-golf__social-icon:nth-child(2) {
    transition-delay: calc(var(--transition-delay) + .05s);
  }
  .fr-profile-card-golf__social-icon:nth-child(3) {
    transition-delay: calc(var(--transition-delay) + .075s);
  }

}
