.fr-note {
  padding: var(--card-padding, var(--space-m));
  border-radius: var(--card-radius, var(--radius));
  background: var(--warning-light, #FDEBB5);
  color: var(--warning-ultra-dark, #544002);
  margin-inline: auto;
}

.fr-note * + :is(h2, h3, h4) {
  margin-top: var(--heading-spacing);
  color: var(--text-dark);
}

.fr-note a {
  text-decoration: underline;
}

.slide-navigation-alpha {
  /* Accordion Spacing */
  --item-gap: .75em;
  /* Icon Styling */
  --icon-url: url('/wp-content/uploads/arrow-right-01-stroke-sharp.svg');
  --icon-size: 1.25em;
  --icon-position: var(--icon-left);
  --icon-gap: var(--icon-gap-right);
  --icon-gap-offset: .25em;
  --icon-color: currentColor;
  --icon-start-rotation: 0deg;
  --icon-rotation: 90deg;
  --icon-order: 0;
  /* List Styling */
  --list-indent: calc(var(--icon-size) / 2);
  /* Link Styling */
  --link-font-size: var(--text-s);
  --link-color: var(--text-dark-muted);
  --link-color-hover: var(--primary-hover);
  --link-border-width: 1px;
  --link-border-style: solid;
  --link-padding-inline: 1em;
  --link-padding-block: var(--item-gap);
  --link-line-height: 1.2;
  --link-decoration: none;
  /* Internal Use */
  --icon-left: 50% auto auto 0;
  --icon-gap-left: 0 calc(var(--icon-size) + var(--icon-gap-offset));
  --icon-right: 50% 0 auto auto;
  --icon-gap-right: calc(var(--icon-size) + var(--icon-gap-offset)) 0;
  inline-size: 100%;
  gap: var(--item-gap);

  /* Animate opening */
  @media (prefers-reduced-motion: no-preference) {
    interpolate-size: allow-keywords;
  }


  /* Add cursor to summary */
  summary {
  	cursor: pointer;
	}

  /* Bullet style */
  details > summary {
    list-style: none;
    position: relative;
    padding-inline: var(--icon-gap);
    line-height: 1.1;
	}
  details > summary::before {
    content: '';
    position: absolute;
    inset: var(--icon-position);
    width: var(--icon-size); 
    height: var(--icon-size);
    -webkit-mask-image: var(--icon-url);
    mask-image: var(--icon-url);
    -webkit-mask-repeat: no-repeat;
    mask-repeat: no-repeat;
    -webkit-mask-position: center;
    mask-position: center;
    -webkit-mask-size: contain;
    mask-size: contain;
    background-color: var(--icon-color, currentColor);
    transition: transform calc(var(--transition-duration, .3s) / 1.5) var(--transition-timing, ease-in-out);
    transform: translateY(-50%) rotate(var(--icon-start-rotation));
	}
  details[open] > summary::before {
    transform: translateY(-50%) rotate(var(--icon-rotation));
	}

  details > summary {
    margin-block-end: var(--item-gap);
  }
}

/* Hide marker style in Safari */
.slide-navigation-alpha details > summary::-webkit-details-marker,
.slide-navigation-alpha details > summary::marker {
    display: none;
    content: '';
}

/* Content opening styling */
.slide-navigation-alpha {
  /**
   * only for Chromium browsers, cannot be nested
   * or it breaks other styles for Safari
   */
  details::details-content {
    opacity: 0;
    block-size: 0;
    overflow-y: clip; 
    transition: 
      content-visibility var(--transition-duration, .3s) allow-discrete,
      opacity var(--transition-duration, .3s),
      block-size var(--transition-duration, .3s);
	}

  details[open]::details-content {
    opacity: 1;
    block-size: auto;
	}

}


/* List and Link Styling */

.slide-navigation-alpha__list {
  margin: 0;
  padding-inline-start: var(--list-indent);
	gap: 0;
}

.slide-navigation-alpha__list-item-link {
  font-size: var(--link-font-size);
  border-left: var(--link-border-width) var(--link-border-style) var(--border-color-dark);
  padding-block: var(--link-padding-block);
  padding-inline: var(--link-padding-inline);
  transition: border-color var(--transition-duration, .3s) var(--transition-timing, ease-in-out), 
    color var(--transition-duration, .3s) var(--transition-timing, ease-in-out);
  line-height: var(--link-line-height);

  &:hover, &[aria-current="page"] {
    border-color: var(--link-color-hover);
    --link-border-style: solid;
  }
}
