settings._cssCustom:
.fr-woo-add-to-cart-alpha {
	--button-color: 			var(--primary);
  --button-color-hover: var(--primary-hover);  
  --button-text: 				var(--text-light);  
  --button-text-hover: 	var(--text-light); 
  --cart-gap: 					var(--grid-gap);
}

.fr-woo-add-to-cart-alpha {
  display: flex;
  flex-direction: row;
  align-items: center;
	gap: var(--cart-gap);
}

.fr-woo-add-to-cart-alpha a:not(.added_to_cart) {
	background-color: var(--button-color)!important;
  border-color: var(--button-color)!important;
	color: var(--button-text)!important;  
  border-width: var(--btn-border-size)!important;
  border-radius: var(--btn-radius)!important;
  border-style: solid;  
  min-width: var(--btn-width)!important;
  line-height: var(--btn-line-height)!important;  
  letter-spacing: var(--btn-letter-spacing)!important;
  text-transform: var(--btn-text-transform)!important;
  text-decoration: var(--btn-text-decoration)!important;
	font-style: var(--btn-text-style)!important;
  font-weight: var(--btn-weight)!important;
  padding: var(--btn-pad-y) var(--btn-pad-x)!important;
}
.fr-woo-add-to-cart-alpha a:hover:not(.added_to_cart)  {
	background-color: var(--button-color-hover)!important;
  border-color: var(--button-color-hover)!important;
	color: var(--button-text-hover)!important;   
}
